export default {
  name: 'vue-tabs-with-active-line',
  props: {
    currentTab: {
      type: String,
      required: true
    },
    tabs: {
      type: Array,
      required: true
    },
    updated: {
      type: [Boolean, String, Array],
      default: undefined
    },
    wrapperClass: {
      type: String,
      required: false
    },
    tabClass: {
      type: String,
      required: false
    },
    tabActiveClass: {
      type: String,
      required: false
    },
    lineClass: {
      type: String,
      required: false
    }
  },
  watch: {
    currentTab: function currentTab(newCurrentTab) {
      if (this.newTab === newCurrentTab) return;
      this.moveActiveLine(newCurrentTab);
    },
    updated: function updated() {
      this.moveActiveLine(this.currentTab);
    }
  },
  data: function data() {
    return {
      activeLineWidth: 0,
      activeLineOffset: 0,
      newTab: ''
    };
  },
  methods: {
    handleClick: function handleClick(value) {
      this.$emit('onClick', value);
      this.moveActiveLine(value);
      this.newTab = value;
    },
    moveActiveLine: function moveActiveLine(newValue) {
      if (!this.currentTab) return;
      var element = this.$refs[newValue][0];
      if (!element) return;
      this.activeLineWidth = element.clientWidth;
      this.activeLineOffset = element.offsetLeft;
    }
  },
  mounted: function mounted() {
    this.moveActiveLine(this.currentTab);
  }
};
