.pad-section {
    margin-top: 10px;
    margin-bottom: 10px;
}

.default-tabs {
    position: relative;
    margin: 0 auto;
}

.default-tabs__item {
    display: inline-block;
    margin: 12px 0px;
    font-size: 1rem;
    color: #003fbd;
    letter-spacing: 0.8px;
    text-decoration: none;
    background-color: transparent;
    cursor: pointer;
    padding: 10px;
}

.default-tabs__item_active {
    color: #003fbd;
}

.default-tabs__item:hover {
    color: #003fbd;
}

.default-tabs__item:focus {
    outline: none;
    color: #003fbd;
}

.default-tabs__item:first-child {
    margin-left: 0;
}

.default-tabs__item:last-child {
    margin-right: 0;
}

.default-tabs__active-line {
    position: absolute;
    bottom: 0;
    left: 0;
    height: 2px;
    background-color: #003fbd;
}

.tabs-content {
    margin-top: 30px;
    font-size: 20px;
}
