import callapi from "../Common/apicall.js";
export default {
    data: function () {
        return {
            formstate: {},
            model: {
                LimitAmount: 5000000,
                Term: 20
            },
            result: {
                LowIncome: 0,
                PaymentAmount: 0
            }
        };
    },
    methods: {
        twoDigit: function (val) {
            try {
                return val.toFixed(2).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")
            } catch{
                return ''
            }
        },
        fieldClassName: function (field) {
            if (!field) {
                return "";
            }
            if ((field.$touched || field.$submitted) && field.$valid) {
                return "has-success";
            }
            if ((field.$touched || field.$submitted) && field.$invalid) {
                return "has-danger";
            }
        },
        onSubmit: function () {
            $('#result').fadeOut(200);
            if (this.formstate.$invalid) {
                return;
            }
            var response = callapi.post(Server + EndPoint, HomeLoan, this.model, ocpkey).then(response => {
                this.result.LowIncome = response.data.LowIncome;
                this.result.PaymentAmount = response.data.PaymentAmount;
                $('#result').fadeIn(400);
            }).catch(e => {
                this.errors.push(e);
            })
        }
    }
};
